import React, { Component } from 'react';
import './CssReset.css'
import SearchTodo from './components/SearchTodo'
import TodoList from './components/TodoList'
import AddTodo from './components/AddTodo'
import BottomBar from './components/BottomBar'


class App extends Component {

  state = {
    todos: [
      { id: 1, content: 'Buy some milk', undone: false },
      { id: 2, content: 'Play mario cart', undone: true }
    ]
  }

  addTodo = (textTodo) => {

    const newTodo = { content: textTodo, id: Math.random() }
    const todos = [...this.state.todos, newTodo]
    this.setState({
      todos
    })
  }

  deleteTodo = (id) => {
    const todos = this.state.todos.filter(todo => {
      return todo.id !== id;
    })

    this.setState({ todos: todos });
  }

  changeState = (todoToChange) => {
    console.log(todoToChange.id);

    /*if (parentElement.className !== "delete-button") {

      //this.state.todos.filter(todo => { todo.id == id })//установить противоположный undone
    }*/
  }

  render() {
    return (
      <div className="App">
        <main className="main-container">
          <h1 className="main-text">todos</h1>
          <div className="input-area">
            <SearchTodo />
            <TodoList todos={this.state.todos} deleteTodo={this.deleteTodo} changeState={this.changeState} />
            <AddTodo addTodo={this.addTodo} />
            <BottomBar />
          </div>
        </main>
      </div>
    )
  }

}

export default App;
