import React from 'react'

const TodoList = ({ todos, deleteTodo, changeState }) => {

  const handleCheck = (e, undone) => {
    if (undone) {
      this.setState({ checked: !this.state.checked });
    }
    else {
      e.target.checked = true;
    }
  }

  const todoList = todos.length ? (
    todos.map(todo => {
      return (
        <li id={todo.id} className={todo.undone ? ("new-todo") : ("new-todo-done")} key={todo.id} onClick={(e) => { changeState(e.target.parentElement.id) }}>
          <input type="checkbox" className="checkbox"></input>
          <span className="custom-checkbox"></span>
          <h2 className="todo-text">{todo.content}</h2>
          <div className="delete-button" onClick={() => { deleteTodo(todo.id) }}></div>
        </li>
      )
    })
  ) : (
      <div></div>
    );


  return (
    <ul className="todo-list">
      {todoList}
    </ul>
  )
}



export default TodoList
